
function submitRequest(id, studentcourseid, course_title, c_code) {
    Swal.fire({
        title: 'Are you sure?',
        text: "You want to DROP " + course_title + " ?",
        icon: 'warning',
        showCancelButton: true,
        confirmButtonColor: '#5cb85c',
        cancelButtonColor: '#d33',
        confirmButtonText: 'Yes, I am Sure !'
    }).then(function (result) {
        if (result.value) {
            $("#" + id).css("display", "block");
            $("#" + c_code).css("display", "none");
            $.ajax({
                type: "POST",
                url: "/CourseRequest/stdDropCourseRequest",
                contentType: "application/json; charset=utf-8",
                data: '{"id":"' + id + '","studentcourseid":"' + studentcourseid + '","course_title":"' + course_title + '"}',
                dataType: "html",
                success: function (result, status, xhr) {
                    $("#statusDiv").html(result);
                    $("#" + id).css("display", "none");
                    $("#" + c_code).css("display", "block");
                },
                error: function (xhr, status, error) {
                    $("#statusDiv").html("<div class= 'alert alert-danger alert-dismissible' > <button type='button' class='close' data-dismiss='alert'>&times;</button> <strong>ERROR: </strong>" + status + " " + error + " " + xhr.status + " " + xhr.statusText + " </div>")
                    $("#" + id).css("display", "none");
                    $("#" + c_code).css("display", "block");
                }
            });
        }

    });
}